import {React, useState} from "react";
import axios from "axios";
import useInput from "../hooks/useInput";
import "./styles/Nav.css"
import { Link } from "react-router-dom";
import Movies from "./Movies"
 const Nav = () => {

  const search = useInput();
  const [movies, setMovies] = useState([])

  const HandleMovies = (e) => {
    e.preventDefault();
    axios
      .get(`https://www.omdbapi.com/?apikey=959ee6fb&s=${search.value}`)
      .then((res) => res.data)
      .then((mov)=> setMovies(mov.Search))
  };

  return (

    <div class="topnav">
  <Link to="/inicio">
<button class="active" >Home</button>
    </Link>
    <Link to="/fav">
<button class="active" >Tus pelis favoritas</button>
    </Link>
    <Link to="/fav">
<button class="active" >Usuarios registrados</button>
    </Link>
 <form onSubmit={HandleMovies}> <input {...search} type="text" placeholder="Buscador de peliculas"/></form>
    <Movies props={movies} />
</div>
  );
};

export default Nav;
