/* CARD la utilice para mostrar la descripcion de la pelicula seleccionada */

import { React, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const Card = () => {
  const [movId, setMovId] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=d87da86a`)
      .then((res) => res.data)
      .then((mov) => setMovId(mov));
  }, [id]);

  const handleClick = (e) => {
    e.preventDefault();
    axios
      .post("/api/fav", { name: movId.Title, img: movId.Poster })
      .then((data) => {
        console.log(data);
      });
  };

  return (
    <div className="card bla card fav">
      <h3 className="cen">{movId.Title}</h3>
      <p>Actor: {movId.Actors}</p>
      <p>Año: {movId.Year}</p>
      <p>Director: {movId.Director}</p>
      <p>Genero: {movId.Genre}</p>
      <p>Lenguaje: {movId.Language}</p>
      <p>Tipo: {movId.Type}</p>
      <p>Pais: {movId.Country}</p>
      <p>Estreno: {movId.Released}</p>
      <br />
      <form>
        <button onClick={handleClick} type="button" className="btn btn-warning">
          AÑADIR A FAVORITOS
        </button>
      </form>
    </div>
  );
};

export default Card;
