/* CARD la utilice para mostrar la descripcion de la pelicula seleccionada */

import { React, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const SingleProduct = () => {
  const [movId, setMovId] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=d87da86a`)
      .then((res) => res.data)
      .then((mov) => setMovId(mov));
  }, [id]);



  const handleClick = (e) => {
    e.preventDefault();
    axios
      .post("/api/fav", { name: movId.Title, img: movId.Poster })
      .then((data) => {
        console.log(data);
      });
  };

  return (
    <div class="object-center  justify-center grid grid-cols-1 lg:grid-cols-2 gap-6">
    <div class="w-full bg-white rounded-lg sahdow-lg overflow-hidden flex flex-col justify-center md:flex-row">
        <div class="w-full md:w-2/5 h-80">
            <img class="object-center object-cover w-full h-full" src={movId.Poster} alt={movId.Title} />
        </div>
        <div class="w-full md:w-3/5 text-left p-6 md:p-4 space-y-2">
            <p class="text-xl text-gray-700 font-bold">Dany Bailey</p>
            <p class="text-base text-gray-400 font-normal">Software Engineer</p>
            <p class="text-base leading-relaxed text-gray-500 font-normal">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.</p>
            </div>
        </div>
        </div>

  );
};

export default SingleProduct;
