import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Card from "../commons/Card";
const Grid = ({ props }) => {

  const movies = useSelector((state)=> state.movies?.movies)
  console.log("movies",movies?.Search);
  /* ACA EN REDUX USESELECTOR PARA TRAER EL RESULT DE NAVBAR */
  if (!movies) {
    return (
      <h1 className="color centrado has-background-danger">
        NO SE ENCONTRARON RESULTADOS :(
      </h1>
    );
  } else if (movies) {
    return (
      <div className="flex justify-between fle ">
        <h1 className="has-background-success">RESULTADOS:</h1>
        <div className="flex  spaces">
          {movies?.Search.map((mov, i) => {
            return (
              <div key={i} className="p-10">
                <Link to={`/movies/${mov.imdbID}`}>
                  <Card movies={mov} />
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
};

export default Grid;
