import { React, useState, useEffect } from "react";
import Register from "./components/Register";
import Login from "./components/Login";
import Search from "./components/Search";
import { Route } from "react-router-dom";
import Nav from "./components/Navbar";
import axios from "axios";
import Card from "./commons/Card";
import Mininav from "./commons/Mininav";
import Listusers from "./commons/Listusers";
import { useHistory } from "react-router-dom";
import Listfav from "./commons/Listfav";

const App = () => {
  const history = useHistory();
  const [fav, setFav] = useState([]);
  const [user, setUser] = useState([]);

  useEffect(() => {
    axios
      .get("/api/fav")
      .then((res) => res.data)
      .then((favorites) => setFav(favorites));
    axios
      .get("/api/newuser")
      .then((res) => res.data)
      .then((user) => setUser(user));
  }, []);

  const HandleLogout = (e) => {
    e.preventDefault();
    axios.post("/api/logout").then(() => {
      setUser({});
      history.push("/");
      alert("Gracias por visitarnos");
    });
  };

  return (
    <div>
      <Switch>




      </Switch>
      <Route exact path="/">
        <Search />
      </Route>
      <Route path="/register">
        <Register />
      </Route>
      <Route path="/login">
        <Login />
      </Route>
      <Route path="/inicio">
        <Nav handle={HandleLogout} />
      </Route>
      <Route path="/movies/:id">
        <Mininav handle={HandleLogout} />
        <Card />
      </Route>
      <Route path="/fav">
        <Mininav handle={HandleLogout} />
        <Listfav props={fav} />
      </Route>
      <Route path="/users">
        <Mininav handle={HandleLogout} />
        <Listusers props={user} />
      </Route>
    </div>
  );
};

export default App;
