import { React, useState } from "react";
import axios from "axios";
import useInput from "../hooks/useInput";
import "./styles/Nav.css";
import { Link } from "react-router-dom";
import Movies from "./Movies";
const Nav = () => {
  const search = useInput();
  const [movies, setMovies] = useState([]);

  const HandleMovies = (e) => {
    e.preventDefault();
    axios
      .get(`https://www.omdbapi.com/?apikey=959ee6fb&s=${search.value}`)
      .then((res) => res.data)
      .then((mov) => setMovies(mov.Search));
  };

  return (
    <nav class="navbar navbar-expand-lg navbar-dark bg-primary">
  <div class="container-fluid">
    <a class="navbar-brand" href="/inicio"><font style="vertical-align: inherit;"><font style="vertical-align: inherit;">OMBD</font></font></a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarColor01" aria-controls="navbarColor01" aria-expanded="false" aria-label="Navegación de palanca">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarColor01">
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link active" href="/fav"><font style="vertical-align: inherit;"><font style="vertical-align: inherit;">Peliculas favoritas
            </font></font><span class="visually-hidden"><font style="vertical-align: inherit;"><font style="vertical-align: inherit;">(Actual)</font></font></span>
          </a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="/users"><font style="vertical-align: inherit;"><font style="vertical-align: inherit;">Usuarios</font></font></a>
        </li>
      </ul>
      <form class="d-flex">
        <form onSubmit={HandleMovies}> <input {...search} type="text" placeholder="Buscador de peliculas"/></form>
 <div class="row">
    <Movies props={movies} />
    </div>
        <button class="btn btn-secondary my-2 my-sm-0" ><font style="vertical-align: inherit;"><font style="vertical-align: inherit;">Cerrar cesion</font></font></button>
      </form>
    </div>
  </div>
</nav>
  );
};

export default Nav;
