import React from "react";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";
import useInput from "../hooks/useInput";
import { getMovies } from "../state/movies";


const Search = () => {
  const history= useHistory()
  const search = useInput("");
  const dispatch= useDispatch()

  const HandleMovies = (e) => {
    e.preventDefault();
    dispatch(getMovies(search.value))
    .then((data)=>{
      console.log("PELIS", data.payload.Search);
    })
  };

  return (
    <div class="main">
      <div class="px-4 sm:px-8 lg:px-16 xl:px-20 mx-auto">
        <div class="hero">
          <div class="hero-headline flex flex-col items-center justify-center pt-24 text-center">
            <h1 class=" font-bold text-3xl text-white bg-black rounded-2xl p-2">
              Busca las mejores peliculas, tus peliculas favoritas!
            </h1>
          </div>

          <form onSubmit={HandleMovies} class="box pt-6">
            <div class="box-wrapper">
              <div class=" bg-white rounded flex items-center w-full p-3 shadow-sm border border-gray-200">
                <button class="outline-none focus:outline-none hover:bg-red-400">
                  <svg
                    class=" w-5 text-gray-600 h-5 cursor-pointer"
                    fill="none"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    stroke-width="2"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path>
                  </svg>
                </button>
                <input
                  {...search}
                  type="search"
                  placeholder="Buscar peliculas"
                  x-model="q"
                  class="w-full pl-4 text-sm outline-none focus:outline-none bg-transparent"
                />
                <div>
                  <button type="submit">
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      class="h-8 w-6 my-auto m-2"
                      fill="none"
                      viewBox="0 0 24 24"
                      stroke="currentColor"
                    >
                      <path
                        stroke-linecap="round"
                        stroke-linejoin="round"
                        stroke-width="2"
                        d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"
                      />
                    </svg>
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Search;
