/* CARD la utilice para mostrar la descripcion de la pelicula seleccionada */

import { React, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const SingleProduct = () => {
  const [movId, setMovId] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=d87da86a`)
      .then((res) => res.data)
      .then((mov) => setMovId(mov));
  }, [id]);



  const handleClick = (e) => {
    e.preventDefault();
    axios
      .post("/api/fav", { name: movId.Title, img: movId.Poster })
      .then((data) => {
        console.log(data);
      });
  };

  return (
    <div class="w-full bg-black rounded-lg sahdow-lg overflow-hidden flex flex-col justify-center items-center">
            <div>
                <img class="object-center object-cover h-auto w-full" src={movId.Poster} alt="photo" />
            </div>
            <div class="text-center py-8 sm:py-6">
                <p class="text-xl text-gray-700 font-bold mb-2">Dany Bailey</p>
                <p class="text-base text-gray-400 font-normal">Software Engineer</p>
            </div>
        </div>

  );
};

export default SingleProduct;
