import React from "react";
import { Link } from "react-router-dom";
import { Route} from "react-router-dom";
import Card from "../commons/Card"
import axios from "axios";
import useInput from "../"

const Movies = ({ props }) => {

  const [movId, setMovId] = useState([]);
  const search = useInput({})



  const handleId = ()=>{
    axios
    .get(`https://www.omdbapi.com/?apikey=959ee6fb&t=${search.value}`)
    .then((res) => res.data)
    .then((mov) => setMovId(mov));
  }
  return (
    <div className="container">
      <div>
        {props.map((movies, i) => {
          return (
            <ul key={i}>
              <li>
                <Link
                  to={`https://www.omdbapi.com/?apikey=959ee6fb&t=${movies.Title}`}
                >
                  <Route path={`https://www.omdbapi.com/?apikey=959ee6fb&t=${movies.Title}`}> 
                    <Card   />
                  </Route>

                  <img src={movies.Poster} alt={movies.Title} />

                </Link>
              </li>
            </ul>
          );
        })}
      </div>
    </div>
  );
};

export default Movies;
