import { React, useState } from "react";
import axios from "axios";
import useInput from "../hooks/useInput";
import "./styles/Nav.css";
import { Link } from "react-router-dom";
import Movies from "./Movies";
const Nav = () => {
  const search = useInput();
  const [movies, setMovies] = useState([]);

  const HandleMovies = (e) => {
    e.preventDefault();
    axios
      .get(`https://www.omdbapi.com/?apikey=959ee6fb&s=${search.value}`)
      .then((res) => res.data)
      .then((mov) => setMovies(mov.Search));

    axios

  };
console.log(movies)
  return (
    <div class="topnav">
      <Link to="/inicio">
        <button >Home</button>
      </Link>
      <Link to="/fav">
        <button >Tus pelis favoritas</button>
      </Link>
      <Link to="/users">
        <button >Usuarios</button>
      </Link>
      <form onSubmit={HandleMovies}>
        {" "}
        <input {...search} type="text" placeholder="Buscador de peliculas" />
      </form>
        <Movies props={movies} />

    </div>
  );
};

export default Nav;
