import {React, useState, useEffect} from 'react'
import {useParams} from "react-router-dom"
import axios from "axios";
const Card = () => {
    const [movId, setMovId]= useState([])

    const {id} = useParams()


    useEffect(()=> {
        axios
          .get(`https://www.omdbapi.com/?i=${id}&apikey=d87da86a`)
          .then((res) => res.data)
          .then((mov) => setMovId(mov));

    })


    return (
        <div>
            <h3>{movId.Title}</h3>
            <p>Actor: {movId.Actors}</p>
            <p>AÃ±o: {movId.Year}</p>
            <p>Director: {movId.Director}</p>
            <p>Genero: {movId.Genre}</p>
            <p>Lenguaje: {movId.Language}</p>
            <p>Tipo: {movId.Type}</p>
            <p>{movId.Country}</p>
            <p>{movId.Released}</p>
        </div>
    )
}

export default Card
