import axios from "axios";
import React from "react";
import useInput from "../hooks/useInput";
import { Link } from "react-router-dom";
import "./styles/Home.css";

const CreateUser = () => {
  const name = useInput();
  const email = useInput();
  const password = useInput();

  const handleSumbit = (e) => {
    e.preventDefault();

    axios.post("/api/newuser", {
      name: name.value,
      email: email.value,
      password: password.value,
    })
    .then(()=> console.log("usuario creado"))
  };

  return (
    <div className="centrado pading">
      <h1 className="color bla">Nuevo miembro!</h1>
      <div className="layout m-5">
        <form onSubmit={handleSumbit} className="color pading">
          <label className="label">Name</label>
          <br />
          <input {...name} type="text" />
          <br />
          <label className="label my-3 label">Email</label>
          <br />
          <input {...email} type="email" />
          <br />
          <label className="label my-3">Password</label>
          <br />
          <input {...password} type="password" />
          <br />
          <button className="button is-link my-5">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default CreateUser;
