import { React, useState, useEffect } from "react";
import CreateUser from "./components/CreateUser";
import Login from "./components/Login";
import Home from "./components/Home";
import Favorites from "./components/Favorites";
import { Route } from "react-router-dom";
import Nav from "./components/Nav";
import axios from "axios";
import Card from "./commons/Card";
import Mininav from "./commons/Mininav"

const App = () => {
  const [fav, setFav] = useState([]);
  /* TRAIGO TODAS LAS PELIS FAVORITAS */
  useEffect(() => {
    axios
      .get("http://localhost:3001/api/fav")
      .then((res) => res.data)
      .then((favorites) => setFav(favorites));


  }, []);

  return (
    <div>
      <Route exact path="/">
        <Home />
      </Route>

      <Route path="/newuser">
        <CreateUser />
      </Route>
      <Route path="/login">
        <Login />
      </Route>
      <Route path="/inicio">
        <Nav />
      </Route>
      <Route path="/movies/:id">
        <Mininav />
        <Card />
      </Route>
      <Route path="/fav">
        <Mininav />
        <Favorites props={fav} />
      </Route>
      <Route path="/users">
        <Mininav />

      </Route>
    </div>
  );
};

export default App;
