/* CARD la utilice para mostrar la descripcion de la pelicula seleccionada */

import { React, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
const SingleProduct = () => {
  const [movId, setMovId] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=d87da86a`)
      .then((res) => res.data)
      .then((mov) => setMovId(mov));
  }, [id]);

  const handleClick = (e) => {
    e.preventDefault();
    axios
      .post("/api/fav", { name: movId.Title, img: movId.Poster })
      .then((data) => {
        console.log(data);
      });
  };

  return (
    <div class="card bla">
      <div class="card-content">
        <div class="media">
          <div class="media-left">
            <figure class="image is-48x48">
              <img src={movId.Poster} alt={movId.Title} />
            </figure>
          </div>
          <div class="media-content">
            <p class="title is-4">{movId.Title}</p>
          </div>
        </div>
        <div class="content">
          <p>Actor: {movId.Actors}</p>
          <p>Año: {movId.Year}</p>
          <p>Director: {movId.Director}</p>
          <p>Genero: {movId.Genre}</p>
          <p>Lenguaje: {movId.Language}</p>
          <p>Tipo: {movId.Type}</p>
          <p>Pais: {movId.Country}</p>
          <p>Estreno: {movId.Released}</p>
          <br />
          <form>
            <button
              onClick={handleClick}
              type="button"
              className="btn btn-warning"
            >
              AÑADIR A FAVORITOS
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default SingleProduct;
