import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Card from "../commons/Card";
const Grid = ({ props }) => {

  const movies = useSelector
  /* ACA EN REDUX USESELECTOR PARA TRAER EL RESULT DE NAVBAR */
  if (!props) {
    return (
      <h1 className="color centrado has-background-danger">
        NO SE ENCONTRARON RESULTADOS :(
      </h1>
    );
  } else if (props) {
    return (
      <div>
        <h1 className="has-background-success">RESULTADOS:</h1>
        <div className="flex-card spaces">
          {props.map((movies, i) => {
            return (
              <div key={i}>
                <Link to={`/movies/${movies.imdbID}`}>
                  <Card props={movies} />
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
};

export default Grid;
