import axios from 'axios'
import {React, useState, useEffect} from 'react'


const Favorites = () => {
    const [fav, setFav]= useState([])

    useEffect(() =>{
        axios
        .get('api/fav')
        .then((res) => res.data)
        .then((favorites) => setFav(favorites))
    },[])


    return (
        <div>
           {fav.map((elem, i)=>{
               <img src={elem[i].img } alt={elem[i].name}/>
           })}
        </div>
    )
}

export default Favorites
